mapper.select:
SELECT A.feed_id AS feedId, A.writer_id AS writerId, A.created_at AS createdAt, A.contents, A.location, B.nm AS writerNm, B.pic AS writerPic, IF(C.feed_id IS NULL, 0, 1) AS isFav FROM feed A INNER JOIN user B ON A.writer_id=B.user_id LEFT JOIN feed_favorite C ON #{signedUserId}=C.user_id AND A.feed_id=C.feed_id
WHERE A.writer_id=#{profileUserId}
ORDER BY A.created_at DESC LIMIT #{startNum}, #{size}

SELECT pic
FROM feed_pics
WHERE feed_id=#{feed_id}
