mapper.select:
SELECT user_id, uid, upw, nm, pic, created_at, updated_at
FROM user
WHERE uid=#{uid}

mapper.update:
UPDATE user
SET pic=#{picName}
WHERE user_id=#{signedUserId}

mapper.select:
SELECT user_id AS userId, uid, upw, nm, pic, created_at AS createdAt, updated_at AS updatedAt
FROM user


        user_id=#{userId}
